    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String receipt = "####################\n";
        String spaghettipricestring = jSpinner3.getValue().toString();
        int spaghettiprice = Integer.valueOf(spaghettipricestring) * 10;
        String lobsterpricestring = jSpinner4.getValue().toString();
        int lobsterprice = Integer.valueOf(spaghettipricestring) * 15;
        String squidpricestring = jSpinner5.getValue().toString();
        int squidprice = Integer.valueOf(spaghettipricestring) * 20;
        String pizzapricestring = jSpinner6.getValue().toString();
        int pizzaprice = Integer.valueOf(spaghettipricestring) * 5;
        String countryfrom = String.valueOf(jComboBox1.getSelectedItem());
        String roomtype = String.valueOf(jComboBox2.getSelectedItem());
        int roomcost = 0;
        if(roomtype.equals("Single"))
        {
            roomcost = 30;
        }
        else if(roomtype.equals("Double"))
        {
            roomcost = 50;
        }
        else if(roomtype.equals("Family"))
        {
            roomcost = 70;
        }
        else
        {
            roomcost = 100;
        }
        String daysString = jSpinner7.getValue().toString();
        int days = Integer.valueOf(daysString);
        roomcost = roomcost * days;
        int total = spaghettiprice + lobsterprice + squidprice + pizzaprice + roomcost;
        receipt += "Customer Full Name - " + jTextField1.getText() + " " + jTextField2.getText() + "\n" + "Country of Arrival - " + countryfrom  + "\n" + "Room Type - " + roomtype + "\n" + "Days staying - " + daysString + "\n" + "Room Cost - " + roomcost + "$\n" + "Spaghetti - " + spaghettiprice + "$\n" + "Lobster - " + lobsterprice + "$\n" + "Squid - " + squidprice + "$\n" + "Pizza - " + pizzaprice + "$\n" + "Total due - " + total + "$\n" + "####################";
        jTextArea1.setText(receipt);
    }//GEN-LAST:event_jButton1ActionPerformed

